con:config.script:
import com.eviware.soapui.model.testsuite.TestRunner.Status

def tSuite =  testRunner.testCase.testSuite.project.getPropertyValue("TSuite")
def tCase =  testRunner.testCase.testSuite.project.getPropertyValue("TCase")		

def inputValuesList = testRunner.testCase.testSuite.project.getPropertyValue("inputValues").split(',')
def optionsList = testRunner.testCase.testSuite.project.getPropertyValue("options").split(',')
def expectedValuesList = testRunner.testCase.testSuite.project.getPropertyValue("expectedValues").split(',')

def inputValuesListSize = inputValuesList.size()
def count = 0
boolean suiteResult = true

inputValuesList.each() 
{
	context.testCase.testSuite.project.setPropertyValue("inputValue", inputValuesList[count])
	context.testCase.testSuite.project.setPropertyValue("option", optionsList[count])
	def runner = testRunner.testCase.testSuite.project.getTestSuiteByName(tSuite).getTestCaseByName(tCase).getTestStepByName("checkOption").run(testRunner, context)		

	def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
	def result = groovyUtils.getXmlHolder( runner.getResponseContent() ).getNodeValue("//*:word")
	if (result == null) {
			result = "" }			

	//log.info("step " + count + ": Status " + runner.getStatus().toString())
	if (result != expectedValuesList[count] ) {
		log.error ("Step " + count + " is FAILED: actual result - " + result + "; expected result - " + expectedValuesList[count])
		suiteResult = false
     }	else {
     	log.info("Step " + count + "is PASSED " )		
     }
	count++
}

if (!suiteResult) {
	testRunner.status = Status.FAILED
}

def inputValuesList = testRunner.testCase.getPropertyValue("inputValues").split(',')
def inputValuesListSize = inputValuesList.size() 

def optionsList = testRunner.testCase.getPropertyValue("langs").split(' ')
def expectedValuesList = testRunner.testCase.getPropertyValue("expectedValues").split(',')

Integer count = new Integer(testRunner.testCase.getPropertyValue("count"))

inputValuesList.each() {
	testRunner.testCase.setPropertyValue("inputValue", inputValuesList[count])
	testRunner.testCase.setPropertyValue("lang", optionsList[count])
	testRunner.testCase.setPropertyValue("expectValue", expectedValuesList[count])
	testRunner.runTestStepByName("checkOption")

	count++
}

con:configuration.scriptText:
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )

def expectValue = messageExchange.modelItem.testStep.testCase.getPropertyValue( "expectValue")
def result = groovyUtils.getXmlHolder( messageExchange.responseContent ).getNodeValue("//*:word")
if (result == null) {
	result = ""
}

assert result == expectValue

